export interface Address {
  street?: string;
  city?: string;
  postalCode?: string;
  country?: string;
}

export interface OrderSummary {
  id: string;
  date: string;
  status: string;
  total: number;
}

export interface UserPreferences {
  newsletter: boolean;
  defaultMinRating?: number;
}

export interface User {
  id: string;
  username: string;
  email: string;
  fullName?: string;
  defaultAddress?: Address;
  preferences: UserPreferences;
  orders: OrderSummary[];
}

import { Injectable, signal } from '@angular/core';
import { Order } from './dev/dev-products.order.component';

export interface TopProduct { productId: string; name: string; sold: number; revenue: number; }
export interface RecentOrder { id: string; user: string; total: number; createdAt: string; status: string; }
export interface AdminStats { totalUsers: number; totalOrders: number; totalRevenue: number; topProducts: TopProduct[]; recentOrders: RecentOrder[]; }

@Injectable({ providedIn: 'root' })
export class AdminStatsService {
  stats = signal<AdminStats>({
    totalUsers: 0,
    totalOrders: 0,
    totalRevenue: 0,
    topProducts: [],
    recentOrders: []
  });

  constructor() { this.refresh(); }

  refresh() {
    const orders: Order[] = JSON.parse(localStorage.getItem('orders') || '[]');
    const users: any[] = JSON.parse(localStorage.getItem('users') || '[]');

    const totalOrders = orders.length;
    const totalRevenue = orders.reduce((sum, o) => sum + o.total, 0);

    const productMap: Record<string, TopProduct> = {};
    orders.forEach(o => o.items.forEach(i => {
      if (!productMap[i.id]) productMap[i.id] = { productId: i.id.toString(), name: i.name, sold: 0, revenue: 0 };
      productMap[i.id].sold += i.quantity;
      productMap[i.id].revenue += i.quantity * i.price;
    }));
    const topProducts = Object.values(productMap).sort((a,b)=>b.sold-a.sold).slice(0,5);

    const recentOrders: RecentOrder[] = orders.map(o => ({
      id: o.id,
      user: o.items[0]?.name || 'Guest',
      total: o.total,
      createdAt: o.date,
      status: 'Pending'
    })).sort((a,b)=>new Date(b.createdAt).getTime()-new Date(a.createdAt).getTime()).slice(0,5);

    this.stats.set({ totalUsers: users.length, totalOrders, totalRevenue, topProducts, recentOrders });
  }
}

import { createReducer, on } from '@ngrx/store';
import { loginSuccess, logout } from './auth.actions';

export interface AuthState {
  user: any | null;
}

export const initialState: AuthState = {
  user: null,
};

export const authReducer = createReducer(
  initialState,
  on(loginSuccess, (state, { user }) => ({ ...state, user })),
  on(logout, (state) => ({ ...state, user: null }))
);

import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { MatCardModule } from '@angular/material/card';
import { MatIconModule } from '@angular/material/icon';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { MatDatepickerModule } from '@angular/material/datepicker';
import { MatNativeDateModule } from '@angular/material/core';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';

interface MenuItem {
  name: string;
  key: string;
}

interface Stats {
  totalUsers?: number;
  totalOrders?: number;
  totalRevenue?: number;
  topProducts?: { name: string; sold: number; revenue: number }[];
  recentOrders?: { id: number; user: string; total: number; status: string; createdAt: string }[];
}

@Component({
  selector: 'app-admin-dashboard',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    MatCardModule,
    MatIconModule,
    MatInputModule,
    MatButtonModule,
    MatDatepickerModule,
    MatNativeDateModule,
    MatProgressSpinnerModule
  ],
  template: `
<div class="admin-container">
  <!-- Left Column -->
  <aside class="sidebar">
    <h2 class="sidebar-title">ADMIN</h2>
    
    <div class="overview">
      <strong>Overview</strong>
      <div class="search-filters">
        <input type="text" placeholder="Search..." [(ngModel)]="searchQuery">
        <div class="buttons-end">
          <input matInput [matDatepicker]="picker" placeholder="Select Date" [(ngModel)]="selectedDate">
          <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
          <mat-datepicker #picker></mat-datepicker>
          <button mat-icon-button class="icon-btn small"><mat-icon>notifications</mat-icon></button>
          <button mat-icon-button class="icon-btn small"><mat-icon>chat</mat-icon></button>
        </div>
      </div>
    </div>

    <nav class="menu">
      <button *ngFor="let item of menuItems" 
              (click)="selectItem(item.key)"
              [class.active]="selected === item.key">
        {{ item.name }}
      </button>
    </nav>
  </aside>

  <!-- Right Column -->
  <main class="content">
    <h1>{{ selected | titlecase }}</h1>

    <div class="content-card">
      <!-- Dashboard Stats -->
      <div *ngIf="selected === 'dashboard'">
        <mat-card class="stat-card">
          <h3>Total Users</h3>
          <p>{{ stats.totalUsers }}</p>
        </mat-card>
        <mat-card class="stat-card">
          <h3>Total Orders</h3>
          <p>{{ stats.totalOrders }}</p>
        </mat-card>
        <mat-card class="stat-card">
          <h3>Total Revenue</h3>
          <p>‚Ç¨{{ stats.totalRevenue }}</p>
        </mat-card>
      </div>

      <!-- Analytics -->
      <div *ngIf="selected === 'analytics'">
        <mat-card>
          <p>Analytics charts and graphs would go here.</p>
        </mat-card>
      </div>

      <!-- Products -->
      <div *ngIf="selected === 'products'">
        <mat-card>
          <h3>Top Products</h3>
          <table>
            <tr>
              <th>Name</th><th>Sold</th><th>Revenue</th>
            </tr>
            <tr *ngFor="let p of stats.topProducts">
              <td>{{ p.name }}</td>
              <td>{{ p.sold }}</td>
              <td>‚Ç¨{{ p.revenue }}</td>
            </tr>
          </table>
        </mat-card>
      </div>

      <!-- Orders -->
      <div *ngIf="selected === 'orders'">
        <mat-card>
          <h3>Recent Orders</h3>
          <table>
            <tr>
              <th>Order ID</th><th>User</th><th>Total</th><th>Status</th><th>Created At</th>
            </tr>
            <tr *ngFor="let o of stats.recentOrders">
              <td>{{ o.id }}</td>
              <td>{{ o.user }}</td>
              <td>‚Ç¨{{ o.total }}</td>
              <td>{{ o.status }}</td>
              <td>{{ o.createdAt }}</td>
            </tr>
          </table>
        </mat-card>
      </div>

      <div *ngIf="['offers','inventory','sales','customers','newsletter','settings'].includes(selected)">
        <mat-card>
          <p>Details for <strong>{{ selected }}</strong> will be displayed here.</p>
        </mat-card>
      </div>
    </div>
  </main>
</div>
  `,
  styles: [`
.admin-container { display: flex; min-height: 100vh; font-family: Arial, sans-serif; background: linear-gradient(135deg, #89f7fe 0%, #66a6ff 100%); }
.sidebar { width: 280px; background: linear-gradient(135deg, #099aa4ff 0%, #012a65ff 100%); color: #fff; padding: 20px; display: flex; flex-direction: column; }
.sidebar-title { font-size: 1.8em; font-weight: bold; margin-bottom: 20px; text-align: center; }
.overview { margin-bottom: 30px; }
.overview strong { display: block; margin-bottom: 10px; font-size: 1.1em; }
.search-filters { display: flex; flex-direction: column; gap: 8px; }
.search-filters input { width: 100%; padding: 6px 10px; border-radius: 6px; border: none; }
.buttons-end { display: flex; gap: 8px; align-items: center; justify-content: flex-end; }
.buttons-end input { flex: 1; }
.icon-btn { background-color: #099aa4ff; color: #fff; border-radius: 6px; }
.icon-btn:hover { background-color: #012a65ff; }
.icon-btn.small { width: 32px; height: 32px; min-width: 32px; padding: 0; }
.menu button { width: 100%; text-align: left; padding: 10px 12px; border: none; background: transparent; color: #fff; font-weight: 600; border-radius: 6px; margin-bottom: 4px; cursor: pointer; transition: background 0.2s; }
.menu button.active, .menu button:hover { background-color: rgba(0,0,0,0.1); }
.content { flex: 1; padding: 30px; }
.content h1 { font-size: 2em; margin-bottom: 20px; color: #2c3e50; }
.content-card { display: flex; flex-direction: column; gap: 20px; }
.stat-card { padding: 20px; background-color: #fff; border-radius: 12px; box-shadow: 0 8px 16px rgba(0,0,0,0.1); text-align: center; }
table { width: 100%; border-collapse: collapse; margin-top: 10px; }
table th, table td { border: 1px solid #ccc; padding: 6px 10px; text-align: center; }
  `]
})
export class AdminDashboardComponent implements OnInit {
  menuItems: MenuItem[] = [
    { name: 'Dashboard', key: 'dashboard' },
    { name: 'Analytics', key: 'analytics' },
    { name: 'Products', key: 'products' },
    { name: 'Offers', key: 'offers' },
    { name: 'Inventory', key: 'inventory' },
    { name: 'Orders', key: 'orders' },
    { name: 'Sales', key: 'sales' },
    { name: 'Customers', key: 'customers' },
    { name: 'Newsletter', key: 'newsletter' },
    { name: 'Settings', key: 'settings' }
  ];

  selected = 'dashboard';
  searchQuery = '';
  selectedDate: Date | null = null;

  stats: Stats = {
    totalUsers: 1200,
    totalOrders: 450,
    totalRevenue: 75000,
    topProducts: [
      { name: 'Product A', sold: 120, revenue: 3000 },
      { name: 'Product B', sold: 80, revenue: 2000 },
      { name: 'Product C', sold: 50, revenue: 1500 }
    ],
    recentOrders: [
      { id: 101, user: 'John', total: 150, status: 'Shipped', createdAt: '2025-12-15' },
      { id: 102, user: 'Alice', total: 200, status: 'Pending', createdAt: '2025-12-16' },
      { id: 103, user: 'Bob', total: 120, status: 'Delivered', createdAt: '2025-12-17' }
    ]
  };

  ngOnInit(): void {}
  selectItem(key: string) { this.selected = key; }
}

import { Component, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router, RouterLink } from '@angular/router';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { FormsModule } from '@angular/forms';

interface Product {
  id: number;
  name: string;
  price: number;
  quantity?: number;
  image?: string;
}

interface Order {
  id: string;
  date: string;
  items: Product[];
  total: number;
  discount?: number;
  delivery?: string;
}

@Component({
  standalone: true,
  selector: 'app-dev-checkout',
  imports: [CommonModule, RouterLink, MatCardModule, MatButtonModule, FormsModule],
  template: `
<section class="checkout-section mx-auto px-4 py-10">
  <h2 class="checkout-title">üí≥ Checkout</h2>

  <div class="checkout-grid" *ngIf="items().length > 0">

    <!-- ORDER SUMMARY -->
    <mat-card class="summary-card">
      <mat-card-title>Order Summary</mat-card-title>
      <mat-card-content>
        <div class="summary-item" *ngFor="let p of items()">
          <img [src]="p.image" class="summary-image" />
          <div>
            <p class="product-name">{{ p.name }}</p>
            <p>Qty: {{ p.quantity }}</p>
            <p>Price: ‚Ç¨{{ p.price }}</p>
          </div>
        </div>

        <hr>

        <!-- COUPON -->
        <label>Coupon Code</label>
        <input class="input" [(ngModel)]="coupon" placeholder="Enter coupon code">
        <button class="btn apply-btn" (click)="applyCoupon()">Apply</button>
        <p *ngIf="discount > 0">Discount applied: -‚Ç¨{{ discount.toFixed(2) }}</p>

        <!-- DELIVERY OPTIONS -->
        <label>Delivery Options</label>
        <select class="input" [(ngModel)]="deliveryOption">
          <option value="standard">Standard (‚Ç¨5)</option>
          <option value="express">Express (‚Ç¨10)</option>
          <option value="pickup">Store Pickup (‚Ç¨0)</option>
        </select>

        <h3 class="total">Total: ‚Ç¨{{ totalWithDiscount() }}</h3>
      </mat-card-content>
    </mat-card>

    <!-- CUSTOMER & PAYMENT -->
    <mat-card class="payment-card">
      <mat-card-title>Customer Information</mat-card-title>
      <mat-card-content>

        <label>Email</label>
        <input class="input" [(ngModel)]="email" placeholder="example@email.com">

        <label>Street Address</label>
        <input class="input" [(ngModel)]="street" placeholder="123 Main Street">

        <label>City</label>
        <input class="input" [(ngModel)]="city" placeholder="Paris">

        <label>Postal Code</label>
        <input class="input" [(ngModel)]="postal" placeholder="75000">

        <label>Country</label>
        <input class="input" [(ngModel)]="country" placeholder="France">

        <hr class="divider">

        <mat-card-title>Payment Details</mat-card-title>

        <label>Name on Card</label>
        <input class="input" [(ngModel)]="cardName" placeholder="John Doe">

        <label>Card Number</label>
        <input class="input" [(ngModel)]="cardNumber" placeholder="1234 5678 9012 3456">

        <div class="row">
          <div>
            <label>Expiry</label>
            <input class="input" [(ngModel)]="expiry" placeholder="MM/YY">
          </div>
          <div>
            <label>CVC</label>
            <input class="input" [(ngModel)]="cvc" placeholder="123">
          </div>
        </div>

        <button class="btn pay-btn" (click)="pay()">Pay Now</button>

      </mat-card-content>
    </mat-card>

  </div>

  <!-- EMPTY CART -->
  <div *ngIf="items().length == 0" class="empty">
    <p>Your cart is empty.</p>
    <button routerLink="/dev/products" class="btn browse-btn">Browse Products</button>
  </div>

  <div class="back-container">
    <button routerLink="/dev/cart" class="btn back-btn">‚Üê Back to Cart</button>
  </div>
</section>
  `,
  styles: [`
:host {
  display: block;
  min-height: 100vh;
  background: linear-gradient(135deg, #89f7fe 0%, #66a6ff 100%);
  padding: 40px 20px;
}
.checkout-section {
  background: rgba(255, 255, 255, 0.85);
  border-radius: 12px;
  padding: 30px;
  max-width: 1100px;
  margin: auto;
}
.checkout-title {
  text-align: center;
  font-size: 2em;
  margin-bottom: 20px;
}
.checkout-grid {
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 20px;
}
@media (max-width:900px) { .checkout-grid { grid-template-columns: 1fr; } }
.summary-card, .payment-card {
  background: rgba(255, 255, 255, 0.95);
  padding: 20px;
  border-radius: 10px;
}
.summary-item {
  display: flex;
  gap: 10px;
  margin-bottom: 15px;
}
.summary-image {
  width: 80px;
  height: 80px;
  object-fit: cover;
  border-radius: 8px;
}
.product-name { font-weight: bold; }
.total {
  text-align: right;
  font-size: 1.4em;
  margin-top: 20px;
}
.input {
  width: 100%;
  padding: 10px;
  margin: 6px 0 14px;
  border-radius: 6px;
  border: 1px solid #bbb;
}
.divider {
  margin: 20px 0;
  border: none;
  border-top: 1px solid #ddd;
}
.row {
  display: flex;
  gap: 15px;
}
.btn {
  padding: 10px 18px;
  border-radius: 8px;
  color: white;
  font-weight: bold;
  border: none;
  cursor: pointer;
  margin-top: 10px;
  transition: 0.2s;
}
.pay-btn { width: 100%; background-color: #2ecc71; }
.pay-btn:hover { background-color: #27ae60; }
.apply-btn { background: #3498db; width: 100%; margin-bottom: 10px; }
.browse-btn { background: #ffb347; }
.back-btn { background: #666; }
.back-btn:hover { background: #444; }
.empty { text-align: center; }
  `]
})
export class DevCheckoutComponent {
  readonly items = signal<Product[]>([]);

  // form fields
  email = '';
  street = '';
  city = '';
  postal = '';
  country = '';
  cardName = '';
  cardNumber = '';
  expiry = '';
  cvc = '';

  // coupon & delivery
  coupon = '';
  discount = 0;
  deliveryOption: 'standard' | 'express' | 'pickup' = 'standard';

  constructor(private router: Router) {
    this.load();
  }

  load() {
    this.items.set(JSON.parse(localStorage.getItem('cart') || '[]'));
  }

  total() {
    let sum = this.items().reduce((acc, p) => acc + p.price * (p.quantity || 1), 0);
    return sum;
  }

  totalWithDiscount() {
    let total = this.total();
    let deliveryCost = 0;
    if (this.deliveryOption === 'standard') deliveryCost = 5;
    else if (this.deliveryOption === 'express') deliveryCost = 10;
    return total - this.discount + deliveryCost;
  }

  applyCoupon() {
    if (this.coupon.trim().toUpperCase() === 'MYSHOP') {
      this.discount = this.total() * 0.1; // 10% discount
      alert('Coupon applied! 10% discount.');
    } else {
      this.discount = 0;
      alert('Invalid coupon code.');
    }
  }

  pay() {
    if (!this.email || !this.street || !this.city || !this.postal || !this.country ||
        !this.cardName || !this.cardNumber || !this.expiry || !this.cvc) {
      alert("Please fill all fields.");
      return;
    }

    const orders: Order[] = JSON.parse(localStorage.getItem("orders") || "[]");

    orders.push({
      id: Date.now().toString(),
      date: new Date().toLocaleString(),
      items: this.items(),
      total: this.totalWithDiscount(),
      discount: this.discount,
      delivery: this.deliveryOption
    });

    localStorage.setItem("orders", JSON.stringify(orders));
    localStorage.removeItem("cart");

    alert("Payment successful!");
    this.router.navigate(['/dev/orders']);
  }
}

import { Component, signal, Inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { MatDialog, MatDialogModule, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { AdminStatsService } from '../admin-stats.service';

export interface OrderItem { id: number; name: string; price: number; quantity: number; image?: string; }
export interface Order { id: string; date: string; items: OrderItem[]; total: number; }

@Component({
  selector: 'order-item-details-dialog',
  standalone: true,
  imports: [CommonModule, MatCardModule, MatButtonModule],
  template: `
    <mat-card class="dialog-card">
      <img *ngIf="data.image" [src]="data.image" class="dialog-image" />
      <h2>{{ data.name }}</h2>
      <p><strong>Price:</strong> <span class="old-price">‚Ç¨{{ data.price }}</span> ‚Üí <span class="new-price">‚Ç¨{{ discountedPrice(data.price) }}</span> <span class="discount">(10% OFF)</span></p>
      <p><strong>Quantity:</strong> {{ data.quantity }}</p>
      <p class="desc">This is a demo description for the ordered item.</p>
      <button mat-flat-button color="primary" (click)="close()">Close</button>
    </mat-card>
  `,
  styles: [`
    .dialog-card { padding: 20px; border-radius: 12px; text-align: center; }
    .dialog-image { width: 100%; height: 250px; object-fit: cover; border-radius: 10px; margin-bottom: 15px; }
    .old-price { text-decoration: line-through; color: #d9534f; }
    .new-price { color: #27ae60; font-weight: bold; }
    .discount { color: #e67e22; margin-left: 4px; }
    .desc { margin-top: 10px; font-size: 0.9rem; color: #555; }
  `]
})
export class OrderItemDetailsDialog {
  constructor(@Inject(MAT_DIALOG_DATA) public data: OrderItem, private dialog: MatDialog) {}
  discountedPrice(price: number) { return Math.round(price * 0.9); }
  close() { this.dialog.closeAll(); }
}

@Component({
  standalone: true,
  selector: 'app-dev-orders',
  imports: [CommonModule, RouterLink, MatCardModule, MatButtonModule, MatDialogModule],
  template: `
<section class="orders-section">
  <h2 class="title">üì¶ My Orders</h2>

  <p *ngIf="orders().length === 0" class="empty-text">You haven't placed any orders yet.</p>

  <div class="orders-list" *ngIf="orders().length > 0">
    <div class="order-card" *ngFor="let o of orders()">
      <mat-card class="order-card-inner">
        <div class="order-header">
          <div>
            <strong>Order #{{ o.id }}</strong>
            <span class="order-date">Placed on {{ o.date }}</span>
            <br>
            <span class="delivery">Estimated delivery: {{ getDeliveryRange(o.date) }}</span>
          </div>
          <div class="order-total">Total: <strong>‚Ç¨{{ o.total }}</strong></div>
        </div>

        <div class="items">
          <div class="item" *ngFor="let i of o.items">
            <img *ngIf="i.image" [src]="i.image" class="item-img" />
            <div class="item-info">
              <p class="item-name">{{ i.name }}</p>
              <p><span class="old-price">‚Ç¨{{ i.price }}</span> ‚Üí <span class="new-price">‚Ç¨{{ discountedPrice(i.price) }}</span> <span class="discount">(10% OFF)</span></p>
              <p>Qty: {{ i.quantity }}</p>
              <button mat-flat-button color="primary" (click)="viewItemDetails(i)">üëÅ View Details</button>
            </div>
          </div>
        </div>

        <div class="order-actions">
          <button mat-flat-button class="btn delete-btn" (click)="deleteOrder(o.id)">üóë Delete Order</button>
        </div>
      </mat-card>
    </div>
  </div>

  <div class="back">
    <button mat-flat-button class="btn back-btn" routerLink="/dev/products">‚Üê Back to Products</button>
  </div>
</section>
  `,
  styles: [`
:host { display: block; min-height: 100vh; background: linear-gradient(135deg, #89f7fe 0%, #66a6ff 100%); padding: 40px 20px; font-family: 'Arial', sans-serif; }
.title { text-align: center; font-size: 2rem; margin-bottom: 30px; color: #2c3e50; }
.orders-section { max-width: 1000px; margin: auto; }
.orders-list { display: grid; gap: 20px; }
.order-card-inner { padding: 20px; border-radius: 12px; background: rgba(255,255,255,0.95); box-shadow: 0 6px 18px rgba(0,0,0,0.15); transition: transform 0.2s, box-shadow 0.2s; }
.order-card-inner:hover { transform: translateY(-4px); box-shadow: 0 12px 24px rgba(0,0,0,0.25); }
.order-header { display: flex; justify-content: space-between; align-items: flex-start; margin-bottom: 15px; border-bottom: 1px solid #ddd; padding-bottom: 10px; }
.order-date { font-size: 0.9rem; color: #555; }
.delivery { font-size: 0.9rem; color: #2c3e50; font-weight: bold; }
.item { display: flex; gap: 15px; margin-bottom: 15px; padding: 10px; border-radius: 10px; background: #ADD8E6; }
.item-img { width: 80px; height: 80px; object-fit: cover; border-radius: 10px; }
.old-price { text-decoration: line-through; color: #d9534f; }
.new-price { color: #27ae60; font-weight: bold; }
.discount { color: #e67e22; margin-left: 4px; }
.item-name { font-weight: bold; }
.order-actions { margin-top: 15px; text-align: right; }
