export class DevAuthComponent {
  readonly token = signal<TokenResponse | null>(null);
  readonly err = signal<string | null>(null);
  readonly loading = signal(false);

  isLoggedIn(): boolean {
    return !!this.token();
  }

  maskToken(token?: string): string {
    if (!token) return '';
    return token.length > 8 ? `****${token.slice(-4)}` : token;
  }

  async login(): Promise<void> {
    this.err.set(null);
    this.token.set(null);
    this.loading.set(true);

    try {
      const res = await fetch('/api/auth/token/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: 'username', password: 'sanoh' }),
      });
      if (!res.ok) throw new Error(`${res.status} ${res.statusText}`);
      const data = await res.json() as TokenResponse;

      // store tokens
      localStorage.setItem('access', data.access);
      localStorage.setItem('refresh', data.refresh);

      this.token.set(data);
    } catch (err: any) {
      this.err.set(err.message || 'Failed to login.');
    } finally {
      this.loading.set(false);
    }
  }

  async refresh(): Promise<void> {
    this.err.set(null);
    this.loading.set(true);

    try {
      const refreshToken = localStorage.getItem('refresh') || 'mock-refresh-token';
      const res = await fetch('/api/auth/token/refresh/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ refresh: refreshToken }),
      });
      if (!res.ok) throw new Error(`${res.status} ${res.statusText}`);
      const data = await res.json() as RefreshResponse;

      // update token
      const current = this.token() || { access: '', refresh: refreshToken };
      const newToken = { ...current, access: data.access };
      localStorage.setItem('access', data.access);
      this.token.set(newToken);
    } catch (err: any) {
      this.err.set(err.message || 'Failed to refresh.');
    } finally {
      this.loading.set(false);
    }
  }
}

import { Injectable, signal } from '@angular/core';
import { Order } from './dev/dev-products.order.component';

export interface TopProduct { productId: string; name: string; sold: number; revenue: number; }
export interface RecentOrder { id: string; user: string; total: number; createdAt: string; status: string; }
export interface AdminStats { totalUsers: number; totalOrders: number; totalRevenue: number; topProducts: TopProduct[]; recentOrders: RecentOrder[]; }

@Injectable({ providedIn: 'root' })
export class AdminStatsService {
  stats = signal<AdminStats>({
    totalUsers: 0,
    totalOrders: 0,
    totalRevenue: 0,
    topProducts: [],
    recentOrders: []
  });

  constructor() { this.refresh(); }

  refresh() {
    const orders: Order[] = JSON.parse(localStorage.getItem('orders') || '[]');
    const users: any[] = JSON.parse(localStorage.getItem('users') || '[]');

    const totalOrders = orders.length;
    const totalRevenue = orders.reduce((sum, o) => sum + o.total, 0);

    const productMap: Record<string, TopProduct> = {};
    orders.forEach(o => o.items.forEach(i => {
      if (!productMap[i.id]) productMap[i.id] = { productId: i.id.toString(), name: i.name, sold: 0, revenue: 0 };
      productMap[i.id].sold += i.quantity;
      productMap[i.id].revenue += i.quantity * i.price;
    }));
    const topProducts = Object.values(productMap).sort((a,b)=>b.sold-a.sold).slice(0,5);

    const recentOrders: RecentOrder[] = orders.map(o => ({
      id: o.id,
      user: o.items[0]?.name || 'Guest',
      total: o.total,
      createdAt: o.date,
      status: 'Pending'
    })).sort((a,b)=>new Date(b.createdAt).getTime()-new Date(a.createdAt).getTime()).slice(0,5);

    this.stats.set({ totalUsers: users.length, totalOrders, totalRevenue, topProducts, recentOrders });
  }
}

export interface User {
  name: string;
}

export interface Address {
  street?: string;
  city?: string;
  postalCode?: string;
  country?: string;
}

export interface OrderSummary {
  id: string;
  date: string;
  status: string;
  total: number;
}

export interface UserPreferences {
  newsletter: boolean;
  defaultMinRating?: number;
}

export interface User {
  id: string;
  username: string;
  email: string;
  fullName?: string;
  defaultAddress?: Address;
  preferences: UserPreferences;
  orders: OrderSummary[];
}

