.footer {
  background: #0f172a;
  color: #cbd5f5;
  padding: 70px 20px 40px;
}

.footer-container {
  max-width: 1200px;
  margin: auto;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 40px;
}

h3 {
  color: #fff;
  font-weight: 700;
  margin-bottom: 16px;
}

.subtitle {
  font-size: 0.95rem;
