  transition: color 0.3s;
}

ul li:hover {
  color: #38bdf8;
}

.subscribe-box {
  display: flex;
  margin-top: 15px;
}

.subscribe-box input {
  flex: 1;
  padding: 10px;
  border-radius: 6px 0 0 6px;
  border: none;
  outline: none;
}

