/* ===== SLIDER ===== */
.slider {
  position: relative;
  height: 520px;
  overflow: hidden;
}
.slide {
  position: absolute;
  inset: 0;
  opacity: 0;
  transition: opacity 1s;
}
.slide.active {
  opacity: 1;
  z-index: 1;
}
.slide-bg {
  width: 100%;
  height: 100%;
  object-fit: cover;
}
.slide-content {
  position: absolute;
  inset: 0;
  display: flex;
  align-items: center;
  padding: 40px;
  color: #fff;
  background: rgba(0,0,0,0.35);
}
.slide-text h1 {
  font-size: 3.2rem;
  margin-bottom: 12px;
}
.slide-text p {
  font-size: 1.4rem;
  margin-bottom: 20px;
}

/* ===== FEATURES ===== */
.feature-container {
  display: flex;
  justify-content: space-around;
  padding: 80px 20px;
  background: linear-gradient(135deg, #3a5f99, #133766);
  flex-wrap: wrap;
}
.feature {
  color: white;
  width: 300px;
}
.feature-item {
  display: flex;
  gap: 15px;
}
.feature-icon {
  width: 60px;
  height: 60px;
  background: rgba(255,255,255,0.2);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
}
.feature-icon svg {
  width: 32px;
  height: 32px;
}

/* ===== PRODUCTS ===== */
.products-section {
  padding: 80px 20px;
  text-align: center;
}

.section-title {
  font-size: 2.6rem;
  font-weight: 700;
  margin-bottom: 40px;
}

.products-carousel {
  display: flex;
