import { Component } from '@angular/core'
import { CommonModule } from '@angular/common'
import { ReactiveFormsModule, FormBuilder, Validators } from '@angular/forms'
import { MatFormFieldModule } from '@angular/material/form-field'
import { MatInputModule } from '@angular/material/input'
import { MatButtonModule } from '@angular/material/button'
import { MatIconModule } from '@angular/material/icon'
import { RouterModule, Router } from '@angular/router'

import { getAuth, signInWithPopup, GoogleAuthProvider, FacebookAuthProvider } from 'firebase/auth'

@Component({
  selector: 'app-login-page',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    MatFormFieldModule,
    MatInputModule,
    MatButtonModule,
    MatIconModule,
    RouterModule
  ],
  templateUrl: './login-page.html',
  styleUrls: ['./login-page.css']
})
export class LoginPageComponent {
  hidePassword = true
  loginForm: any

  constructor(private fb: FormBuilder, private router: Router) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    })
  }

  // Normal login with registered email/password
  onSubmit() {
    if (this.loginForm.invalid) {
      this.loginForm.markAllAsTouched()
      return
    }

    const { email, password } = this.loginForm.value
    const savedUser = JSON.parse(localStorage.getItem('user') || '{}')

    if (savedUser.email === email && savedUser.password === password) {
      alert('Login successful! Welcome back!')
      localStorage.setItem('access', 'mock-token')
      this.router.navigate(['/'])
    } else {
      alert('Invalid email or password. Please sign up first.')
    }
  }

  // Google social login
  loginWithGoogle() {
    const auth = getAuth()
    const provider = new GoogleAuthProvider()
    signInWithPopup(auth, provider)
      .then(result => {
        const user = result.user
        localStorage.setItem('access', 'mock-token')
        alert(`Welcome ${user.displayName || user.email}! You are logged in.`)
        this.router.navigate(['/'])
      })
      .catch(err => alert(err.message))
  }

  // Facebook social login
  loginWithFacebook() {
    const auth = getAuth()
    const provider = new FacebookAuthProvider()
    signInWithPopup(auth, provider)
      .then(result => {
        const user = result.user
        localStorage.setItem('access', 'mock-token')
        alert(`Welcome ${user.displayName || user.email}! You are logged in.`)
        this.router.navigate(['/'])
      })
      .catch(err => alert(err.message))
  }
}

styleUrlsData.data:
:host {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  margin: 0;
  background: linear-gradient(135deg, #89f7fe 0%, #66a6ff 100%);
  font-family: 'Roboto', sans-serif;
}

/* Wrapper */
.login-wrapper {
  width: 100%;
  max-width: 1000px;
  padding: 20px;
  box-sizing: border-box;
}

/* Container */
.login-container {
  display: flex;
  width: 100%;
  height: 70vh;
  border-radius: 12px;
  overflow: hidden;
  background: rgba(255, 255, 255, 0.8);
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  backdrop-filter: blur(4px);
}

/* Left pane */
.left-pane {
  flex: 1;
  padding: 50px 30px;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.left-pane h2 {
  margin-bottom: 30px;
  color: #2c3e50;
  font-size: 2em;
  text-align: center;
}

/* Form */
mat-form-field {
  width: 100%;
  margin-bottom: 20px;
}

input.mat-input-element {
  color: #000;
}

/* Submit button */
.full-width {
  width: 100%;
}

/* =========================
   SOCIAL LOGIN (ONE ROW)
========================= */
.social-login {
  margin-top: 15px;
  display: flex;
  gap: 10px;
}

/* Social buttons */
button.social-btn {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  font-weight: 500;
  text-transform: none;
}

/* SVG icons */
.social-icon {
  width: 20px;
  height: 20px;
  display: block;
}

/* Brand borders */
button.social-btn.google {
  border-color: #1a6d22;
}

button.social-btn.facebook {
  border-color: #1877f2;
}

/* =========================
   SIGN UP LINK
========================= */
.signup-link {
  margin-top: 15px;
  text-align: center;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 10px;
  font-size: 0.9em;
}

.signup-link span {
  color: #555;
}

/* =========================
   RIGHT PANE
========================= */
.right-pane {
  flex: 1;
  display: none;
}

.right-pane img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  opacity: 0.85;
}

/* Desktop view */
@media (min-width: 768px) {
  .right-pane {
    display: block;
  }
}

